import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'

let style = {
  marginLeft: '7%'
}

export default function Navigation() {

  const [currentURL, setCurrentURL] = useState(window.location.href);
  const [lineStyle, setLineStyle] = useState({marginLeft: '7%', width: '0'});

  useEffect(() => {
    if(currentURL.includes('about')) {
      setLineStyle({marginLeft: '7%'})
    } else if (currentURL.includes('skills')) {
      setLineStyle({marginLeft: '40%'})
    } else {
      setLineStyle({marginLeft: '75%'})
    }
  },[currentURL])

  const setStyle = (margin, visible=false) => {
    const width = visible ? '20%' : '0';
    setLineStyle({marginLeft: margin, width});
    console.log('margin', margin);

  }

  return (
    <div className="navigation">
      <div className='nav-left-space'>Jon Langkammer</div>
      <ul>
        <li className='nav-link one' 
          onClick={() => setCurrentURL(window.location.href)}
          onMouseEnter={() => setStyle('7%')}
          >
          <Link to="/about" className='nav-links' >About</Link>
        </li>
        <li className='nav-link two' 
          onClick={() => setCurrentURL(window.location.href)}
          onMouseEnter={() => setStyle('40%')}
          >
          <Link to="/skills" className='nav-links' >Skills</Link>
        </li>
        <li className='nav-link three' 
          onClick={() => setCurrentURL(window.location.href)}
          onMouseEnter={() => setStyle('7%')}
          >
          <Link to="/projects" className='nav-links' >Projects</Link>
        </li>
        <hr className='hr' style={style}/>
      </ul>
      <div className='nav-right-space'></div>
    </div>
  )
}
