import React, { useEffect, Fragment } from 'react';
import ProjectDetails from '../Components/ProjectDetails';
import { Image, Reveal } from 'semantic-ui-react';
import Page from '../Components/Page';
import { projects } from '../Components/constants'

export default function Projects(props) {

    useEffect(() => {
        window.scrollTo(0, 0)
    })

    return (
        <Page background="linear-gradient(90deg, #FFFFFF  0%, #BCBCBC 100%)">
            <div className='card-container'>
                {projects.map(project => {
                    return (
                        <Fragment>
                            <div key={project.name} className='project-header'>{project.name}</div>
                            <Reveal animated='fade'>
                                <Reveal.Content visible>
                                    <Image className='profile-img' alt='oh no' src={project.image}></Image>
                                </Reveal.Content>
                                <Reveal.Content hidden>
                                    <ProjectDetails projectInfo={project} />
                                </Reveal.Content>
                            </Reveal>
                        </Fragment>
                    )
                })}
            </div>
        </Page>
    )
}
