import React, { useState, useEffect } from 'react'
import './App.scss'
import { Tech, About, Projects } from './Pages/index';
import NotFound from './Pages/NotFound'
import { BrowserRouter as Router, Route, Redirect, Switch } from 'react-router-dom'
import Navbar from './Components/Navbar'
import Footer from './Components/Footer';
import Helmet from 'react-helmet';
import ReactPlayer from 'react-player';
import { 
  CSSTransition, 
  TransitionGroup 
} from 'react-transition-group';

import url from './Audio/pop.wav'

function App() {

  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [playing, setPlaying] = useState(false);

  useEffect(() => {
    window.addEventListener('resize', updateWidth);
  })

  const updateWidth = () => {
    setWindowWidth(window.innerWidth)
  }

  const play = (rate = 1) => {
    !playing && setPlaying(true);
  }

  return (
    <div>
      <Helmet>
        <style>{'body { background-image: linear-gradient(90deg, #FFFFFF  0%, #BCBCBC 100%); }'}</style>
      </Helmet>
      <ReactPlayer 
        url={url} 
        playing={playing}
        onEnded={() => setPlaying(false)}
        volume={0.3}
        loop={false}/>
      <Router>
        <Navbar windowWidth={windowWidth} play={play}/>
        <Route
          render={({ location }) => {
            const { pathname } = location;
            return (
              <TransitionGroup>
                <CSSTransition
                  key={pathname}
                  classNames="page"
                  timeout={{
                    enter: 750,
                    exit: 750
                  }}
                >
                  <Route
                    location={location}
                    render={() => (
                      <Switch>
                        <Route exact path='/' render={() => <Redirect to='/about' />} />
                        <Route exact path='/tech' render={() => <Tech play={play} windowWidth={windowWidth}/>} />
                        <Route exact path='/about' render={() => <About play={play} windowWidth={windowWidth}/>} />
                        <Route exact path='/projects' render={() => <Projects windowWidth={windowWidth}/>} />
                        <Route exact path='/404' render={() => <NotFound />} />
                        <Route path='*' render={() => <Redirect to='/404' />} />
                      </Switch>
                    )}
                  />
                </CSSTransition>
              </TransitionGroup>
            )
          }}
        />
      </Router>
      <Footer />
    </div>
  );
}

export default App;
